mapper.select:
select
*
from sys_news
where
1 = 1

    and title like CONCAT(CONCAT('%', #{pd.title}),'%')


    and publisher like CONCAT(CONCAT('%', #{pd.publisher}),'%')


    and addtime >= #{pd.lastStart}


    and addtime <= #{pd.lastEnd}


    and recommand=#{pd.recommand}


    and status=#{pd.status}

order by sequence desc
