mapper.select:
select a.NAME, a.CODE, a.P_ID, a.PCODE, a.CLASS, a.ORDERS, a.CREATEDATE, a.PERSONNUM, a.LEADER, a.ADDRESS, a.PHONE, a.EMAIL, a.URL, a.INTRO, a.REMARK, a.DEFAULTONE, a.DEFAULTTWO, a.DEFAULTTHREE, a.ORG_ID from TB_ORG a left join TB_ORG b on a.P_ID = b.ORG_ID where a.P_ID = #{pd.P_ID}
and a.NAME LIKE CONCAT(CONCAT('%', #{pd.NAME}),'%')
order by a.ORDERS

select a.NAME, a.CODE, a.P_ID, a.PCODE, a.CLASS, a.ORDERS, a.CREATEDATE, a.PERSONNUM, a.LEADER, a.ADDRESS, a.PHONE, a.EMAIL, a.URL, a.INTRO, a.REMARK, a.DEFAULTONE, a.DEFAULTTWO, a.DEFAULTTHREE, a.ORG_ID from TB_ORG a left join TB_ORG b on a.P_ID = b.ORG_ID where a.P_ID = #{pd.P_ID}
and a.NAME LIKE CONCAT(CONCAT('%', #{pd.NAME}),'%')
order by a.ORDERS
